t.script:
document.addEventListener('DOMContentLoaded', function () {
    document.querySelectorAll('.hoverable-row').forEach(function (row) {
        row.addEventListener('click', function () {
            const qty = parseFloat(row.dataset.qty);
            const input = document.querySelector('input[name="add_qty"], #quantity');

            if (input) {
                input.value = qty;

                // Déclenche tous les événements nécessaires
                ['input', 'change', 'blur'].forEach(ev =>
                    input.dispatchEvent(new Event(ev, { bubbles: true }))
                );

                // Forcer recalcul pour Owl ou autres thèmes
                const recalcButton = document.querySelector('.oe_website_sale .js_product');
                if (recalcButton) {
                    recalcButton.click();
                }
            }

            // Effet visuel
            document.querySelectorAll('.hoverable-row').forEach(r => r.classList.remove('selected-row'));
            row.classList.add('selected-row');
        });
    });
});
