xpath.script:
document.addEventListener('DOMContentLoaded', function () {
    const rows = document.querySelectorAll('.hoverable-row');
    const input = document.querySelector('input[name="add_qty"], #quantity');

    function highlightMatchingRow(qty) {
        let matched = null;
        rows.forEach(function(row) {
            const rowQty = parseFloat(row.dataset.qty);
            if (!isNaN(rowQty) && qty >= rowQty) {
                matched = row;
            }
        });

        rows.forEach(function(r) {
            r.classList.remove('selected-row');
        });
        if (matched) {
            matched.classList.add('selected-row');
        }
    }

    if (input) {
        input.addEventListener('input', function() {
            const qty = parseFloat(input.value);
            if (!isNaN(qty)) {
                highlightMatchingRow(qty);
            }
        });
    }

    rows.forEach(function(row) {
        row.addEventListener('click', function () {
            const qty = parseFloat(row.dataset.qty);
            if (!isNaN(qty) && input) {
                input.value = qty;
                input.dispatchEvent(new Event('input'));
            }
        });
    });
});
